{
  "name" : "moti.dbc.profile.v1",
  "jsonschema" : "{
    "$id": "moti.dbc.profile.v1",
     "description": "profile sample",
     "type": "object",
     "properties": {
         "type": {
             "type": "string",
             "description": "Profile type"
         },
         "favorites": {
             "type": "object",
             "properties" : {
                "cameras" : {
                    "type": "array",
                    "items" : {
                        "type": "string"
                    },
                    "minItems": 0,
                    "uniqueItems": true
                },
                "routes" : {
                    "type": "array",
                    "items": {
                        "type": "object",
                        "properties": {
                            "start": {
                                "type": "string"
                            },
                            "end": {
                                "type": "string"
                            },
                            "geofence_id": {
                                "type":  "string"
                            }
                        },
                        "required": ["start", "end", "geofence_id"]
                    },
                    "minItems": 0,
                    "uniqueItems": true
                }
             }
         }
    },
     "required": ["type", "favorites"]
}",
  "description" : "cool address validator"
}

